                    command.Connection = conn;
                    externalReader = command.ExecuteReader();
                    if (externalReader.Read())
                    {
                        retval = true;
                    }
                }
                catch (Exception ex)
                {
                    throw;
                }
                finally
                {
                    if (externalReader != null)
                        externalReader.Close();
                    if (conn != null)
                        conn.Close();
                }
            }
            return retval;
        }
